mapper.select:
select COP_NO, MEM_NO, COP_TYPE, COP_USED, COP_USEDATE
from coupon
where mem_no = #{value}

select m.mem_no, m.mem_id, m.mem_name, m.mem_birthdate, m.mem_email, m.mem_phoneno, m.mem_mileage, m.mem_membertype, m.mem_joindate
from member m
where m.mem_id = #{value}

select m.mem_no, m.mem_id, m.mem_name, m.mem_birthdate, m.mem_email, m.mem_phoneno, m.mem_mileage, m.mem_membertype, m.mem_joindate
from member m
where m.mem_no = #{value}

mapper.update:
update member
set MEM_MILEAGE=#{memMileage}
where mem_id=#{memId}
