mapper.select:
select COP_NO, MEM_NO, COP_TYPE, COP_USED, COP_USEDATE
from coupon
where mem_no = #{value}

select m.mem_no, m.mem_id, m.mem_name, m.mem_birthdate, m.mem_email, m.mem_phoneno, m.mem_mileage, m.mem_membertype, m.mem_joindate
from member m
where m.mem_id = #{value}

select mem_no, mem_id, mem_name, mem_birthdate, mem_email, mem_phoneno, mem_mileage, mem_membertype, mem_joindate from ( select ceil(rownum/#{contentsPerPage}) page, mem_no, mem_id, mem_name, mem_birthdate, mem_email, mem_phoneno, mem_mileage, mem_membertype, mem_joindate from ( select mem_no, mem_id, mem_name, mem_birthdate, mem_email, mem_phoneno, mem_mileage, (select commoncode.cmn_code_kor from commoncode where commoncode.cmn_no = mem_membertype) mem_membertype, mem_joindate from member
where ${searchType} = #{searchKeyword}
where ${searchType} like '%'||#{searchKeyword}||'%'
order by mem_no ) ) where page = #{pageNo}

mapper.update:
update member
set MEM_MILEAGE=#{memMileage}
where mem_id=#{memId}

mapper.select:
select count(mem_no) from member

	where ${searchType} = #{searchKeyword}


	where ${searchType} like '%'||#{searchKeyword}||'%'
