mapper.select:
select COP_NO, MEM_NO,  COP_TYPE, COP_USED, (select commoncode.cmn_code_kor from commoncode where commoncode.cmn_no = COP_TYPE) COP_TYPE_NAME, (select commoncode.cmn_code_kor from commoncode where commoncode.cmn_no = COP_USED) COP_USED_VALUE, COP_USEDATE
from coupon
where mem_no = #{value}

select mem_no, mem_id, mem_name, mem_birthdate, mem_email, mem_phoneno, mem_mileage, (select commoncode.cmn_code_kor from commoncode where commoncode.cmn_no = mem_membertype) mem_membertype_name, mem_membertype, mem_joindate
from member
where mem_id = #{value}

select mem_no, mem_id, mem_name, mem_birthdate, mem_email, mem_phoneno, mem_mileage, (select commoncode.cmn_code_kor from commoncode where commoncode.cmn_no = mem_membertype) mem_membertype_name, mem_membertype, mem_joindate
from member
where mem_no = #{value}

select mem_no, mem_id, mem_name, mem_birthdate, mem_email, mem_phoneno, mem_mileage, mem_membertype_name, mem_membertype, mem_joindate from ( select ceil(rownum/#{contentsPerPage}) page, mem_no, mem_id, mem_name, mem_birthdate, mem_email, mem_phoneno, mem_mileage, mem_membertype_name, mem_membertype, mem_joindate from ( select mem_no, mem_id, mem_name, mem_birthdate, mem_email, mem_phoneno, mem_mileage, (select commoncode.cmn_code_kor from commoncode where commoncode.cmn_no = mem_membertype) mem_membertype_name, mem_membertype, mem_joindate from member
where ${searchType} = #{searchKeyword}
where ${searchType} like '%'||#{searchKeyword}||'%'
order by mem_no ) ) where page = #{pageNo}

mapper.update:
update member
set MEM_MILEAGE=#{memberMileage}
where mem_id=#{memberId}

mapper.select:
select count(mem_no) from member

	where ${searchType} = #{searchKeyword}


	where ${searchType} like '%'||#{searchKeyword}||'%'
