mapper.select:
select 	e.evt_no, e.evt_name, e.evt_startdate, e.evt_enddate, e.evt_regdate, e.evt_title
from		event e
where e.evt_no = ${value}

select 	evt_no, evt_name, evt_startdate, evt_enddate, evt_regdate, evt_title
from (
	select ceil(rownum/#{contentsPerPage}) page, evt_no, evt_name, evt_startdate, evt_enddate, evt_regdate, evt_title
	from (
			
			order by evt_no
	)
) 
where page = #{pageNo}
