mapper.select:
select s.sch_no, s.sch_no, s.mr_no, s.sch_date, m.thea_no from schedule s, movieroom m where s.mr_no = m.mr_no
and m.thea_no = #{value}
order by m.thea_no, s.sch_no

select SUBSTR(s.sch_date, 12) sch_date, s.sch_no from schedule s, movieroom mr, theater t where 1=1 and mr.mr_no = s.mr_no and mr.thea_no = t.thea_no
and mr.thea_no = #{theaNo}
and SUBSTR(s.sch_date, 1, 10) = #{schDate}
and s.sch_date > to_char(sysdate, 'yyyy-mm-dd hh24:mi:ss') order by s.sch_date

		select id, password, name, email, join_date
		from (
			select ceil(rownum/#{contentsPerPage}) page, id, password, name, email, join_date
			from (

					order by join_date
			)
		) 
		where page = #{pageNo}

